import React, { useState, useEffect } from 'react';
import CMC_KEY from '../keys'

const App = () => {

  const [coins, setCoins] = useState([])

  useEffect( async () => {
    const result = await fetch(`https://pro-api.coinmarketcap.com/v1/cryptocurrency/listings/latest?CMC_PRO_API_KEY=${CMC_KEY}`)
      .then(res => res.json())
      .then(obj => setCoins(obj.data))
  })

  return (
    <div>
      {coins && coins.map(coin => <p>{coin.name}</p>)}
    </div>
  )
}

export default App;
